        <div class="w-full max-w-[500px] mx-auto lg:max-w-none px-8 sm:p-10 py-8 md:px-8 lg:px-[7rem] lg:w-[50%]">
            <h2 class="text-2xl font-semibold text-center text-gray-700 dark:text-white">Hi There !</h2>
            <p class="text-xl text-center text-gray-600 dark:text-gray-200 mt-4 capitalize">Forgot Your Password ?</p>
            <p class="text-sm text-center text-gray-500 dark:text-gray-400 mt-8">Enter your email ID here so that we can send you with a password reset link.</p>


            <div class="mt-6 relative">
                <label
                    class="block mb-2 text-sm font-medium text-gray-600 dark:text-gray-200"
                    for="email"
                >Email Address</label>
                <input
                    id="email"
                    class="block w-full px-4 py-3 text-gray-700 bg-white border rounded-md dark:bg-[#333] dark:text-gray-300 dark:border-gray-600 focus:border-blue-400 focus:ring-opacity-40 dark:focus:border-blue-300 focus:outline-none focus:ring focus:ring-blue-300"
                    type="email"
                    :class="{'border !border-red-500 bg-red-50 dark:bg-[#332929]':v$.email.$error}"
                    v-model="data.email"
                    placeholder="john@doe.com"
                />
                <p v-if="v$.email.$error" class="text-sm absolute -bottom-6 text-red-400 lowercase">{{ v$.email.$errors[0].$message }}</p>
            </div>

            <div class="mt-10">
                <button
                    @click="getPasswordResetLink"
                    class="w-full p-3 tracking-wide text-white transition-colors duration-200 transform bg-[#333] hover:bg-black dark:bg-indigo-700 rounded dark:hover:bg-indigo-500 focus:outline-none focus:bg-gray-600"
                >Get password reset link</button>
            </div>


            <div class="flex items-center justify-between mt-8">
                <span class="w-1/5 border-b border-gray-300 dark:border-gray-600 md:w-1/4"></span>
                <span class="text-xs text-gray-500 uppercase dark:text-gray-400">or</span>
                <span class="w-1/5 border-b border-gray-300 dark:border-gray-600 md:w-1/4"></span>
            </div>

            <div class="mt-6 flex justify-center">
                <router-link to="/auth/register"
                    class="text-xs uppercase hover:underline dark:text-gray-300 hover:text-blue-700 dark:hover:text-indigo-400">Register
                    here</router-link>
            </div>
            <p class="mt-4 text-[12px] text-gray-500 flex justify-center">
                <router-link to="/auth/login"
                    class="text-[12px] ml-2 hover:underline hover:text-indigo-700 dark:hover:text-indigo-400">already registered ?</router-link>
            </p>
        </div>
    </div>
</template>

<script setup>
import { useAuthStore } from '../../stores/auth.store'; 
import useVuelidate from '@vuelidate/core';
import { required, email } from '@vuelidate/validators';
import { useRouter } from 'vue-router';
import { useNotification } from '../../utils/notification';
import { reactive } from 'vue';
// props & emits

// variables
const authStore = useAuthStore();
const router = useRouter();
const data = reactive({
    email:''
});
const rules = {
    email:{ required, email }
}
const v$ = useVuelidate(rules, data );
const notification = useNotification();

// computed & watchers

// functions
async function getPasswordResetLink(){
    const valid = await v$.value.$validate();
    if(valid){
        const res = await authStore.requestPassowrdResetLink(data);
        notification.notify({
            type: res.success ? 'success' : 'error',
            message: res.data.message
        });
        if(res.success){
            data.email = '';
            router.push('/auth/login');
        }
    }
}

// lifecycles

</script>


