<script setup>
import useVuelidate from "@vuelidate/core";
import { required, email, sameAs, helpers } from "@vuelidate/validators";
import { ref, reactive, computed } from "vue";
import { useAuthStore } from "../../stores/auth.store.js";
import { useRouter } from 'vue-router';

// variables
const authStore = useAuthStore();
const step = ref(1);
const togglePassword = ref(false);
const loading = ref(false);
const router = useRouter();

// User data
const data = reactive({
  first_name: "",
  last_name: "",
  email: "",
  bio: "The best developer",
  password: "",
  password_confirmation: "",
});

const uppercase = helpers.regex(/[A-Z]/);
const lowercase = helpers.regex(/[a-z]/);
const digit = helpers.regex(/[0-9]/);
const mineight = helpers.regex(/.{8,}/);
const special = helpers.regex(/\W|_/);

// Validations
const rules = {
  first_name: { required },
  last_name: { required },
  email: { required, email },
  password: {
    required,
    uppercase: helpers.withMessage(
      "Password must consist an uppercase",
      uppercase
    ),
    lowercase: helpers.withMessage(
      "Password must consist an lowercase",
      lowercase
    ),
    digit: helpers.withMessage("Password must consist a digit", digit),
    special: helpers.withMessage(
      "Password must consist a special character",
      special
    ),
    mineight: helpers.withMessage(
      "Password must be atleast 8 chars long",
      mineight
    ),
  },
  password_confirmation: {
    required,
    sameAs: helpers.withMessage(
      "it must match password",
      sameAs(computed(() => data.password))
    ),
  },
};
const v$ = useVuelidate(rules, data);

async function register() {
  let valid = false;
  if (step.value == 1) {
    const validations = await Promise.all([
      v$.value.first_name.$validate(),
      v$.value.last_name.$validate(),
    ]);
    valid = validations[0] && validations[1];
    console.log(valid);
  }
  if (step.value == 2) {
    valid = await v$.value.email.$validate();
  }
  if (step.value == 3) {
    const valid = await v$.value.$validate();
    if (valid) {
      loading.value = true
      let res = await authStore.register(data);
      loading.value = false
      if(res.success){
        router.push('/auth/login')
      }
    }
  }
  if (valid && step.value < 3) {
    step.value++;
  }
}
</script>


