<script setup>
import { useAuthStore } from "../../stores/auth.store";
import useVuelidate from "@vuelidate/core";
import { required, helpers, sameAs } from "@vuelidate/validators";
import { useRouter, useRoute } from "vue-router";
import { useNotification } from "../../utils/notification";
import { reactive, ref, computed } from "vue";
import { onMounted } from "@vue/runtime-core";
import { useErrorStore } from "../../stores/error.store";

// props & emits

// variables
const authStore = useAuthStore();
const errorStore = useErrorStore();
const router = useRouter();
const route = useRoute();
const notification = useNotification();
const togglePassword = ref(false);
const data = reactive({
    token: "",
    password: "",
    password_confirmation: "",
});
const uppercase = helpers.regex(/[A-Z]/);
const lowercase = helpers.regex(/[a-z]/);
const digit = helpers.regex(/[0-9]/);
const mineight = helpers.regex(/.{8,}/);
const special = helpers.regex(/\W|_/);
const rules = {
    token: { required },
    password: {
        required,
        uppercase: helpers.withMessage(
            "Password must consist an uppercase",
            uppercase
        ),
        lowercase: helpers.withMessage(
            "Password must consist an lowercase",
            lowercase
        ),
        digit: helpers.withMessage("Password must consist a digit", digit),
        special: helpers.withMessage(
            "Password must consist a special character",
            special
        ),
        mineight: helpers.withMessage(
            "Password must be atleast 8 chars long",
            mineight
        ),
    },
    password_confirmation: {
        required,
        sameAs: helpers.withMessage(
            "it must match password",
            sameAs(computed(() => data.password))
        ),
    },
};
const v$ = useVuelidate(rules, data);

// computed & watchers

// functions
async function reset() {
    console.log("Reset password");
    const valid = await v$.value.$validate();
    if (valid) {
        const res = await authStore.resetPassword(data);
        notification.notify({
            type: res.success ? "success" : "error",
            message: res.data.message,
        });
        if (res.success) {
            data.email = "";
            router.push("/auth/login");
        }
    }else{
        console.log(v$.value);
    }
}

// lifecycles
onMounted(() => {
    data.token = route.query.token;
    if (!data.token) {
        errorStore.handle({
            response: {
                status: 404,
            },
        })
    }
});
</script>

